mapper.select:
SELECT 

FROM mk2_popularize_common_member a

WHERE a.id = #{id}

SELECT
FROM mk2_popularize_common_member a
AND a.user_id = #{userId}
AND a.mail LIKE '%'||#{mail}||'%' '%'+#{mail}+'%' concat('%',#{mail},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.release_over = #{releaseOver}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE mk2_popularize_common_member SET
	release_begin_date = #{releaseBeginDate},
	release_cycle_ratio = #{releaseCycleRatio}
WHERE id = #{id}

UPDATE mk2_popularize_common_member SET
	release_over = #{releaseOver},
	release_volume = #{releaseVolume}
WHERE id = #{id} AND update_date = #{updateDate}
