mapper.select:
SELECT 

FROM address_config a

WHERE a.id = #{id}

SELECT
FROM address_config a
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE address_config SET 	
	name = #{name},
	status = #{status},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}
